[asy]
unitsize(0.5 cm);

pair A, B, C, D, E, F, G;

A = (0,0);
C = (7,1);
E = rotate(60)*(C);
G = (A + C + E)/3;
B = 2*G - E;
D = 2*G - A;
F = 2*G - C;

draw(A--B--C--D--E--F--cycle);
draw((-2,0)--(9,0));
draw((0,-2)--(0,8));
